import DOMPurify from "dompurify";

import { useNavigate, useParams } from 'react-router-dom';
const html = `
"<p><strong>Aim to have roughly $0 left in your bank account when you die – otherwise you’ve saved too much</strong></p>
<p><u>Money is a resource that helps you live your best life – not an end in itself</u></p>
<p>Invest in experiences when you’re young, to get compounding returns on your memories.<img alt="laugh" src="http://127.0.0.1:8000http://127.0.0.1:8000/static/ckeditor/ckeditor/plugins/smiley/images/teeth_smile.png" style="height:23px; width:23px" title="laugh"/></p>
<p><samp><em>Die With Zero</em> is a practical guide for getting the most out of your money, and your short time on earth. It helped me realise that money is only as valuable as the life experiences you buy with it. Spend your life obsessing about your bank balance, and you’ve missed the point.</samp></p>
<p> </p>
<p><em><span style="background-color:#e74c3c">Die With Zero</span></em><span style="background-color:#e74c3c"> is definitely for people who have a decent income, some financial security, and have already looked into saving up. Also for young people planning their long-term future.</span></p>"

`
const ContentBook = () => {
    const params = useParams();
    const [booksContent, SetbooksContent] = React.useState([])
    React.useEffect(()=>{
        getBookContent({id:params.id}).then((res)=>{
            SetbooksContent(booksContent)
            console.log('getBookContent',res)
        }).catch((err)=>{

        })
    },[])
